// Get all elements with animation properties
  const animatedElements = Array.from(wwLib.getFrontDocument().querySelectorAll('[style*="animation"]'));

  // Create an Intersection Observer
  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      // If the element is intersecting (visible)
      if (entry.isIntersecting) {
        // Add a class to start the animation
        entry.target.style.visibility = 'visible';
        entry.target.style.opacity = '1';
        // Stop observing this element
        observer.unobserve(entry.target);
      }
    });
  }, {
    root: null, // viewport
    threshold: 0.1, // trigger when 10% of the element is visible
    rootMargin: '0px 0px -100px 0px' // slightly before element enters viewport
  });

  // Set initial state for all animated elements
  animatedElements.forEach(element => {
    // Set initial state - invisible
    element.style.visibility = 'hidden';
    element.style.opacity = '0';

    // Start observing the element
    observer.observe(element);
  });

  // Return a cleanup function
  return () => {
    animatedElements.forEach(element => {
      observer.unobserve(element);
    });
  };
